/**Responsible for contacting the server
 * and returning all the users
 */
const loadUsers = async () => {
  const newData = await fetch("/users", {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
  }).then((res) => res.json());
  return newData;
};

/**Responsible for contacting the server
 * and confirming the login data a user
 * provided are accepted
 */
const verifyUser = async (user) => {
  const newData = await fetch("/login", {
    method: "POST",
    body: JSON.stringify({ name: user.username, password: user.password }),
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
  }).then((res) => res.json());
  return newData;
};

/**Responsible to get users notifications/info
 */
const getUser = async (id) => {
  const newData = await fetch("/getUser", {
    method: "POST",
    body: JSON.stringify({ id: id }),
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
  }).then((res) => res.json());
  return newData;
};

/**Responsible for contacting the server
 * and adding a user
 */
const createUser = async (user) => {
  await fetch("/createUser", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
    body: JSON.stringify({ ...user }),
  }).then((res) => res.json());
  return;
};

/**Responsible for contacting the server
 * and confirming a user registration
 */
const confirmUser = async (id) => {
  const newData = await fetch("/confirmUser", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
    body: JSON.stringify({ id: id }),
  }).then((res) => res.json());
  return newData;
};

/**Responsible for contacting the server
 * and updating a user
 */
const updateUser = async (user) => {
  const newData = await fetch("/updateUser", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
    body: JSON.stringify({ user: user }),
  }).then((res) => res.json());
  return newData;
};

/**Responsible for contacting the server
 * and deleting a user
 */
const deleteUser = async (id) => {
  const newData = await fetch("/deleteUser", {
    method: "POST",
    body: JSON.stringify({ id: id }),
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
  }).then((res) => res.json());
  return newData;
};

module.exports = {
  loadUsers,
  verifyUser,
  getUser,
  createUser,
  confirmUser,
  deleteUser,
  updateUser,
};
